# FILENAME: /SentientGridAlliance/CivicGovernance/global-future-city-layout-v1.0.aln
city_opt_in {
    node_id: uuid-v4
    country_iso: "XX"
    status: ("active"|"inactive"|"paused")
    sovereignty: ("local"|"federal"|"alliance-determined")
}
citizen_id_system {
    uuid: auto
    consent_status: ("opt-in"|"opt-out")
    background_audit: { pass:bool, infractions:[], credits:[], last_audit:timedate }
    job_eligibility: auto-calculate_by_policy
}
augmented_weapons_registry {
    serial_hash: sha256,
    owner_uuid: auto,
    status: ("inactive"|"active"|"revoked"),
    auth_level: ("citizen-certified"|"law-enforcement"|"bounty-approved"),
    compliance_doc: pdf
}
reputation_engine {
    uuid, score, last_update, source:[verified_event|law|peer]
}
security_monitor {
    enclave_id, event, severity, node_action(rollback|quarantine|lockout|notify)
}
law_enforcement_api {
    request_id, query_reason, permitted_access ("background-full"|"credit-only"), log_trace, oversight: true
}
federated_update {
    jurisdiction, legal_update, auto-notify opt-in nodes, local-override: always_allowed
}

